import React from 'react';
import './App.css';
import { BsChevronLeft } from "react-icons/bs";
import { IoPowerOutline } from "react-icons/io5";
import { MdAddCircleOutline, MdRemoveCircleOutline } from "react-icons/md";


const Widget2 = props => {

  console.log('list',props.list)

  var aa = props.list.find((v, i)=> i === props.selectedChannel)
  console.log('list2',aa)
    return(
        // <div className="container2">
        <div className="box">
          <div className="d-flex justify-content-between" style={{backgroundColor:'#E4B06E',borderTopRightRadius:'20px',borderTopLeftRadius:'20px',padding:'5px',height:100}}>
            <div className="p-2 col-example text-left">
              <h2 style={{color:'#fff'}}> <BsChevronLeft /></h2>
            </div>
            <div className="p-2 col-example text-left">
              <h3 style={{color:'#F3F6F2'}}>STATIONS</h3>
            </div>
            <div className="p-2 col-example text-left">
              <h2 style={{color:'#F3F6F2'}}> <IoPowerOutline /> </h2>
            </div>
          </div>
          <div className="list">
          {/* <MDBContainer> */}
            {props.list.map((v, i, {length})=> {
              return (
              <div key={i}>
                <div className="d-flex justify-content-between" style={{paddingRight:15,paddingLeft:15}}>
                <p style={{color:'#8E94A3',fontSize:26,fontWeight:'initial'}}>{v.name}</p>
                <p style={{color:'#8E94A3',fontSize:26, fontWeight:'bold'}}>{v.station}</p>
                </div>
                {i === props.selectedChannel ?
                 <div className="d-flex justify-content-around">
                 <div className="p-2 col-example text-left"style={{color:'#8E94A3'}}>
                  <h3 style={{color:'#8E94A3'}}><MdAddCircleOutline/></h3>
                 </div>
                 <div className="p-2 col-example text-left">
                   <img src={v.uri} style={{height:120,width:120, borderRadius:60}} alt="Demo"/>
                  </div>
                 <div className="p-2 col-example text-left">
                  <h2 style={{color:'#8E94A3'}}> <MdRemoveCircleOutline /></h2>
                 </div>
               </div>
                  : null 
                }
                {i + 1 !== length ?
                  <div className="hr"></div>
                  : null 
                }
              </div>
              )
            })}
            </div>
          {/* </MDBContainer> */}

          <div className="footer">
            <span className="block-example border-top border-white" style={{color:'#fff'}}></span>
            <p style={{color:'#E4B06E',marginTop:10}}>CURRENTLY PLAYING</p>
            {props.list.map((v, i)=> {
                // <div>
                 {/* {i === props.selectedChannel ? */}
                    <div key={i}>
                        <p style={{color:'#E4B06E',marginTop:10}}>CURRENTLY PLAYING</p>
                        <p style={{color:'#8E94A3',fontSize:26,fontWeight:'initial'}}>{v.name}</p>
                    </div>
                 {/* : null} */}
                // </div>
            })
        }

          </div>


        </div>
    //   </div>
    );
}

export default Widget2;
