import React from 'react';
import './App.css';
import { BsChevronLeft } from "react-icons/bs";
import { IoPowerOutline } from "react-icons/bs";

const Widget = props => {
    return(
        <div className="container">
        <div className="box">
          <h2>Hello, CodePen!</h2>
          <h3> Lets go for a <BsChevronLeft />? </h3>
          <h3> Lets go for a <IoPowerOutline />? </h3>
          <p>
            Set a background colour <small>(Name or #HEX value)</small>
          </p>
          <p>
            Uncomment the componentDidMount() method to make the colours array auto
            cycle.
          </p>
          <br />

        </div>
      </div>
    );
}

export default Widget;
