import React, {useState} from 'react';
import './App.css';
import { BsChevronLeft } from "react-icons/bs";
import { IoPowerOutline } from "react-icons/io5";
import { MDBContainer } from "mdbreact";
import { MdAddCircleOutline, MdRemoveCircleOutline } from "react-icons/md";
import Widget2 from './Widget2';


const channels =[
  { 'name':'Putin FM', id : 1, 'station': '66,6', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/arctichare.png'},
  { 'name':'Dribble FM', id: 2, 'station': '101,2,6', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/girl.png'}, 
  { 'name': 'Doge FM', id: 3, 'station': '99,4', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/sails.png'},
  { 'name': 'Ballads FM', id: 4, 'station': '87,1', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/boat.png'} ,
  { 'name': 'Maximum FM', id: 5, 'station': '142,2', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/airplane.png'},
]

const Widget = props => {
  const [channel, setChannel] = useState(getWindowDimensions());
  const handleClick = () => {
    console.log('Clicked');
  }
    return(
        // <div className="container" >
        <div style={{display:'flex',flexDirection:'row',justifyContent:'space-evenly',alignContent:'center',padding:50,backgroundColor:'#1a1c29',height:750}}>
        <div className="box">
          <div className="d-flex justify-content-between" style={{backgroundColor:'#E4B06E',borderTopRightRadius:'20px',borderTopLeftRadius:'20px',padding:'5px',height:100}}>
            <div className="p-2 col-example text-left">
              <h2 style={{color:'#fff'}}> <BsChevronLeft /></h2>
            </div>
            <div className="p-2 col-example text-left">
              <h3 style={{color:'#F3F6F2'}}>STATIONS</h3>
            </div>
            <div className="p-2 col-example text-left">
              <h2 style={{color:'#F3F6F2'}}> <IoPowerOutline /> </h2>
            </div>
          </div>
          <div className="list">
            {channels.map((v, i, {length})=> {
              return (
              <div>
                <div onClick={handleClick} className="d-flex justify-content-between" style={{paddingRight:15,paddingLeft:15}}>
                <p style={{color:'#8E94A3',fontSize:26,fontWeight:'initial'}}>{v.name}</p>
                <p style={{color:'#8E94A3',fontSize:26, fontWeight:'bold'}}>{v.station}</p>
                </div>
                {/* {i + 1 === length ?
                 <div className="d-flex justify-content-around">
                 <div className="p-2 col-example text-left"style={{color:'#8E94A3'}}>
                  <h3 style={{color:'#8E94A3'}}><MdAddCircleOutline/></h3>
                 </div>
                 <div className="p-2 col-example text-left">
                   <img src={v.uri} style={{height:120,width:120, borderRadius:60}} alt="Demo"/>
                  </div>
                 <div className="p-2 col-example text-left">
                  <h2 style={{color:'#8E94A3'}}> <MdRemoveCircleOutline /></h2>
                 </div>
               </div>
                  : null 
                } */}
                {i + 1 !== length ?
                  <div className="hr" style={{paddingRight:15,paddingLeft:15}}></div>
                  : null 
                }
              </div>
              )
            })}
            </div>

          <div className="footer">
            <span className="block-example border-top border-white" style={{color:'#fff'}}></span>
          </div>
          </div>
          <div>

        </div>
         <Widget2 list={channels}  selectedChannel={2}/>
       </div>
    );
}

export default Widget;
