import React from 'react';
import styles from './App.css';
import { BsChevronLeft } from "react-icons/bs";
import { IoPowerOutline } from "react-icons/io5";
import { MDBContainer } from "mdbreact";
import { MdAddCircleOutline, MdRemoveCircleOutline } from "react-icons/md";


const Widget = props => {
  const list = [
    {
      id: 'a',
      firstname: 'Robin',
      lastname: 'Wieruch',
      year: 1988,
    },
    {
      id: 'b',
      firstname: 'Dave',
      lastname: 'Davidds',
      year: 1990,
    },
    {
      id: 'a',
      firstname: 'Robin',
      lastname: 'Wieruch',
      year: 1988,
    },
    {
      id: 'b',
      firstname: 'Dave',
      lastname: 'Davidds',
      year: 1990,
    },
    {
      id: 'a',
      firstname: 'Robin',
      lastname: 'Wieruch',
      year: 1988,
    },
    {
      id: 'b',
      firstname: 'Dave',
      lastname: 'Davidds',
      year: 1990,
    },
    {
      id: 'a',
      firstname: 'Robin',
      lastname: 'Wieruch',
      year: 1988,
    },
    {
      id: 'b',
      firstname: 'Dave',
      lastname: 'Davidds',
      year: 1990,
    },
    {
      id: 'a',
      firstname: 'Robin',
      lastname: 'Wieruch',
      year: 1988,
    },
    {
      id: 'b',
      firstname: 'Dave',
      lastname: 'Davidds',
      year: 1990,
    },
    {
      id: 'a',
      firstname: 'Robin',
      lastname: 'Wieruch',
      year: 1988,
    },
    {
      id: 'b',
      firstname: 'Dave',
      lastname: 'Davidds',
      year: 1990,
    },
  ];

  const refs = list.reduce((acc, value) => {
    acc[value.id] = React.createRef();
    return acc;
  }, {});

  const handleClick = id =>
    refs[id].current.scrollIntoView({
      behavior: 'smooth',
      block: 'start',
    });

  var channels =[
    { 'name':'Putin FM', id : 1, 'station': '66,6', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/arctichare.png'},
    { 'name':'Dribble FM', id: 2, 'station': '101,2,6', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/girl.png'}, 
    { 'name': 'Doge FM', id: 3, 'station': '99,4', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/sails.png'},
    { 'name': 'Ballads FM', id: 4, 'station': '87,1', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/boat.png'} ,
    { 'name': 'Maximum FM', id: 5, 'station': '142,2', 'uri':'https://homepages.cae.wisc.edu/~ece533/images/airplane.png'} 
  ]
    return(
        <div className="container">
        <div className="box">
          {/* <div style={styles.topnav}>
            <h1 style={{color:'#fff'}}> <BsChevronLeft /></h1>
            <h1 style={{color:'#fff'}}>STATIONS</h1>
            <h1 style={{color:'#fff'}}> <IoPowerOutline /> </h1>
          </div> */}
          <div className="d-flex justify-content-between" style={{backgroundColor:'#E4B06E',borderTopRightRadius:'20px',borderTopLeftRadius:'20px',padding:'5px'}}>
            <div className="p-2 col-example text-left">
              <h2 style={{color:'#fff'}}> <BsChevronLeft /></h2>
            </div>
            <div className="p-2 col-example text-left">
              <h3 style={{color:'#F3F6F2'}}>STATIONS</h3>
            </div>
            <div className="p-2 col-example text-left">
              <h2 style={{color:'#F3F6F2'}}> <IoPowerOutline /> </h2>
            </div>
          </div>
          {/* <MDBContainer> */}
            {channels.map((v, i, {length})=> {
              return (
              <div>
                <div className="d-flex justify-content-between"id="section1" style={{paddingRight:15,paddingLeft:15}}>
                <h3 style={{color:'#8E94A3'}}>{v.name}</h3>
                <h3 style={{color:'#8E94A3'}}>{v.station}</h3>
                </div>
                {/* {i + 1 === length ?
                 <div className="d-flex justify-content-around">
                 <div className="p-2 col-example text-left"style={{color:'#8E94A3'}}>
                  <h3 style={{color:'#8E94A3'}}><MdAddCircleOutline/></h3>
                 </div>
                 <div className="p-2 col-example text-left">
                   <img src={v.uri} style={{height:120,width:120, borderRadius:60}} alt="Demo"/>
                  </div>
                 <div className="p-2 col-example text-left">
                  <h2 style={{color:'#8E94A3'}}> <MdRemoveCircleOutline /></h2>
                 </div>
               </div>
                  : null 
                } */}
                {i + 1 !== length ?
                  <div className="hr"></div>
                  : null 
                }
              </div>
              )
            })}
             <ul>
    {list.map(item => {
      const ref = React.createRef();

      const handleClick = () =>
        ref.current.scrollIntoView({
          behavior: 'smooth',
          block: 'start',
        });

      return (
        <li
          key={item.id}
          ref={ref}
          style={{ height: '250px', border: '1px solid black' }}
        >
          <div>{item.id}</div>
          <div>{item.firstname}</div>
          <div>{item.lastname}</div>
          <div>{item.year}</div>
          <button type="button" onClick={handleClick}>
            Scroll Into View
          </button>
        </li>
      );
    })}
  </ul>
          {/* </MDBContainer> */}

          <div className="footer">
          <span className="block-example border-top border-white" style={{color:'#fff'}}></span>
          </div>
          <span className="block-example border-top border-white" style={{color:'#fff'}}></span>


        </div>
      </div>
    );
}

export default Widget;
